mapper.select:
select count(*) from testRecipe 

		and ca1=#{ca1}


		and ca2=#{ca2}


		and ca3=#{ca3}


		and ca4=#{ca4}

select * from (
	select rownum as r_num, a.* from (
		select t.*, m.name as writer from testRecipe t, members m

				t.m_idx = m.m_idx

					and t.ca1=#{ca1}


					and t.ca2=#{ca2}


					and t.ca3=#{ca3}


					and t.ca4=#{ca4}


			order by t.r_idx desc
	) a
) where r_num between #{begin} and #{end}
