mapper.select:
select count(*) from recipe

		savepublic = 1

			and a_permission=#{a_permission}


			and (recipe_title like '%'||#{k}||'%' or recipe_introduce like '%'||#{k}||'%' or pack like '%'||#{k}||'%' or material like '%'||#{k}||'%' or ordercontent like '%'||#{k}||'%' or recipe_tip like '%'||#{k}||'%' or recipe_tag like '%'||#{k}||'%')


			and recipe_title like '%'||#{recipe_title}||'%'


			and ca1=#{ca1}


			and ca2=#{ca2}


			and ca3=#{ca3}


			and ca4=#{ca4}

select * from (
	select rownum as r_num, a.* from (
		select r.*, m.name as writer from recipe r, members m

				r.m_idx = m.m_idx and savepublic = 1

					and r.a_permission=#{a_permission}


					and (recipe_title like '%'||#{k}||'%' or recipe_introduce like '%'||#{k}||'%' or pack like '%'||#{k}||'%' or material like '%'||#{k}||'%' or ordercontent like '%'||#{k}||'%' or recipe_tip like '%'||#{k}||'%' or recipe_tag like '%'||#{k}||'%' or m.name like '%'||#{k}||'%')


					and recipe_title like '%'||#{recipe_title}||'%'


					and r.ca1=#{ca1}


					and r.ca2=#{ca2}


					and r.ca3=#{ca3}


					and r.ca4=#{ca4}


			order by r.r_idx desc
	) a
) where r_num between #{begin} and #{end}

select count(*) from recipe r, members m

		r.m_idx = m.m_idx and savepublic = 1 and r.regdate >= #{start} and r.regdate <= #{endt}

			and m.name like '%'||#{name}||'%'


			and m.id like '%'||#{id}||'%'


			and r_idx = #{r_idx}


			and m.email like '%'||#{email}||'%'


			and recipe_title like '%'||#{recipe_title}||'%'


			and a_permission=#{a_permission}



				and recipe_video is null


				and recipe_video is not null

select * from (
	select rownum as r_num, a.* from (
		select r.*, m.name, m.id, m.email from recipe r, members m

				r.m_idx = m.m_idx and savepublic = 1 and r.regdate >= #{start} and r.regdate <= #{endt}

					and m.name like '%'||#{name}||'%'


					and m.id like '%'||#{id}||'%'


					and r_idx = #{r_idx}


					and m.email like '%'||#{email}||'%'


					and recipe_title like '%'||#{recipe_title}||'%'


					and a_permission=#{a_permission}



						and recipe_video is null


						and recipe_video is not null



			order by r.r_idx desc
	) a
) where r_num between #{begin} and #{end}

select * from (
	select rownum r_num, a.* from(
		select * from talk order by t_idx desc ) a
	) where r_num between #{begin} and #{end}

select * from (
	select t.*, name, recipe_title from (
		select r_idx, sum(r_count) r_count from r_rank where r_date >= #{start} and r_date <= #{endt} group by r_idx
	) t, recipe r, members m where t.r_idx = r.r_idx and m.m_idx = r.m_idx and savepublic = 1 and a_permission = 1 order by r_count desc
) where rownum >= 1 and rownum <= 5

select count(*) from qna q, members m

		q.regdate >= #{start} and q.regdate <= #{endt} and q.m_idx = m.m_idx

			and m.id = #{id}


			and m.name = #{name}


			and q.m_idx = #{m_idx}


			and m.email like '%'||#{email}||'%'

select * from (
	select rownum as r_num, a.* from (
		select q.*, m.name, m.id, m.email from qna q, members m

				q.regdate >= #{start} and q.regdate <= #{endt} and q.m_idx = m.m_idx

					and m.id = #{id}


					and m.name = #{name}


					and q.m_idx = #{m_idx}


					and m.email like '%'||#{email}||'%'


		order by q_idx desc
	) a
) where r_num between #{begin} and #{end}

select count(*) from members m

		m.regdate >= #{start} and m.regdate <= #{endt}

			and m.name = #{name}


			and m.id = #{id}


			and m_idx = #{m_idx}


			and m.email = #{email}


			and m.gender = #{gender}

select * from (
	select rownum as r_num, a.* from (
		select * from members

			regdate >= #{start} and regdate <= #{endt}

					and name = #{name}


					and id = #{id}


					and m_idx = #{m_idx}


					and email = #{email}


					and gender = #{gender}


			order by m_idx desc
	) a
) where r_num between #{begin} and #{end}

select * from (
	select rownum as r_num, e.* from (
		select * from event


				and e_end > sysdate and e_start < sysdate


				and e_end < sysdate


				and e_type = #{e_type}


		order by e_start desc
	) e
) where r_num between #{begin} and #{end}

select count(*) from manager

		regdate >= #{start} and regdate <= #{endt}

			and op_idx = #{op_idx}


			and op_id = #{op_id}


			and op_id = #{op_id}


			and op_name = #{op_name}


			and op_phone = #{op_phone}


			and op_com_email = #{op_com_email}


			and op_per_email = #{op_per_email}


			and op_position = #{op_position}

select * from (
	select rownum as r_num, e.* from (
		select * from event

			e_public = 1

				and e_end > sysdate and e_start < sysdate


				and e_end < sysdate


				and e_type = #{e_type}


		order by e_end asc
	) e
) where r_num between #{begin} and #{end}

select * from (
	select rownum as r_num, e.* from (
		select * from event where e_public = 1 and e_type = 2 order by e_regdate desc
	) e
) where r_num between 1 and 3

select * from (
	select rownum as r_num, a.* from (
		select r.*, nvl(count, 0) count from recipe r, (
			select c.r_idx, count(*) count from recipe_c c group by c.r_idx
		) g where r.r_idx = g.r_idx(+) and r.m_idx = #{m_idx} and r.savepublic = #{savepublic}
	) a
) where r_num between #{begin} and #{end}

select * from (
	select rownum as r_num, a.* from (
		select * from manager

			regdate >= #{start} and regdate <= #{endt}

					and op_idx = #{op_idx}


					and op_id = #{op_id}


					and op_id = #{op_id}


					and op_name = #{op_name}


					and op_phone = #{op_phone}


					and op_com_email = #{op_com_email}


					and op_per_email = #{op_per_email}


					and op_position = #{op_position}


			order by op_idx desc
	) a
) where r_num between #{begin} and #{end}
